const scene = await BattleTransition.SelectScene();
if (!scene) return;

new BattleTransition(scene)
  .parallel(
    (sequence) => sequence.linearWipe("left", 1000, "black"),
    (sequence) => sequence.linearWipe("right", 1000, "black"),
    (sequence) => sequence.linearWipe("top", 1000, "black"),
    (sequence) => sequence.linearWipe("bottom", 1000, "black")
  )
  .diamondWipe(40)
  .execute();
